import React, { useState } from "react";
import "./reports.css"
import { useEffect } from "react";
import Select from "react-select";
import Chart from "chart.js/auto"
import { Doughnut, Line,Bar } from "react-chartjs-2"
import axios from "axios";
const Reports = () => {
  const chartoptions = {
    title: {
      display: true,
      text: 'My Chart Title',

      position: 'bottom',
      fontSize: 24

    }

  }
  const [searchfilter, setSearchfilter] = useState({
    serchon: "Sales",
    technology: "",
    title: "",
    users: undefined
  });
  const [options, setOptions] = useState([])
  const [chartdata, setChartdata] = useState({
    labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
    datasets: [
      {
        label: 'expense',
        data: [12, 19, 3, 5, 2, 3],
        // data: [10, 17, 3, 5, 2, 13],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        borderWidth: 1,
      },
      {
        label: 'Income',
        data: [10, 17, 3, 5, 2, 13],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        // borderColor: [
        //   'rgba(255, 99, 132, 1)',
        //   'rgba(54, 162, 235, 1)',
        //   'rgba(255, 206, 86, 1)',
        //   'rgba(75, 192, 192, 1)',
        //   'rgba(153, 102, 255, 1)',
        //   'rgba(255, 159, 64, 1)',
        // ],
        borderWidth: 1,
      },
      {
        label: 'Income2',
        // data: [12, 19, 3, 5, 2, 3],
        data: [10, 17, 3, 5, 2, 13],
        backgroundColor:'pink',
        // borderColor: [
        //   'rgba(255, 99, 132, 1)',
        //   'rgba(54, 162, 235, 1)',
        //   'rgba(255, 206, 86, 1)',
        //   'rgba(75, 192, 192, 1)',
        //   'rgba(153, 102, 255, 1)',
        //   'rgba(255, 159, 64, 1)',
        // ],
        borderWidth: 1,
      },
      {
        label: 'Income3',
        // data: [12, 19, 3, 5, 2, 3],
        data: [10, 17, 3, 5, 2, 13],
        backgroundColor: 'red',
        // borderColor: [
        //   'rgba(255, 99, 132, 1)',
        //   'rgba(54, 162, 235, 1)',
        //   'rgba(255, 206, 86, 1)',
        //   'rgba(75, 192, 192, 1)',
        //   'rgba(153, 102, 255, 1)',
        //   'rgba(255, 159, 64, 1)',
        // ],
        borderWidth: 1,
      },
    ],
  });
  const [chartdatadollar, setChartdatadoallar] = useState({
    labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
    datasets: [
      {
        label: 'expense',
        data: [12, 19, 3, 5, 2, 3],
        // data: [10, 17, 3, 5, 2, 13],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        borderWidth: 1,
      },
      {
        label: 'Income',
        data: [10, 17, 3, 5, 2, 13],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        // borderColor: [
        //   'rgba(255, 99, 132, 1)',
        //   'rgba(54, 162, 235, 1)',
        //   'rgba(255, 206, 86, 1)',
        //   'rgba(75, 192, 192, 1)',
        //   'rgba(153, 102, 255, 1)',
        //   'rgba(255, 159, 64, 1)',
        // ],
        borderWidth: 1,
      },
      {
        label: 'Income2',
        // data: [12, 19, 3, 5, 2, 3],
        data: [10, 17, 3, 5, 2, 13],
        backgroundColor:'pink',
        // borderColor: [
        //   'rgba(255, 99, 132, 1)',
        //   'rgba(54, 162, 235, 1)',
        //   'rgba(255, 206, 86, 1)',
