import axios from 'axios'
import React, { useState, useEffect } from 'react'
import "./c_inquiry.css"

import { useForm } from 'react-hook-form';
import { useNavigate } from 'react-router-dom'
import { sourcelist, technologylist } from "../../constfiles"

export default function Inquiry() {

  const navigate = useNavigate()
  const refreshPage = () => {
    window.location.reload(false);
  }
  const [vendorMessage, setVendorMessage] = useState(false)
  const [personAdded, setPersonAdded] = useState(false)
  const [personMessage, setPersonMessage] = useState(false)

  const [modal, setModal] = useState(false)
  const [message, setMessage] = useState(false)
  const [err, setError] = useState({
  })
  const [inquiryform, SetInquiryForm] = useState({
  })
  const [media, SetMedia] = useState({})
  const [vendorform, SetVendorForm] = useState({
    vCompanyName: "",
    vAddress: "",
    vEmail: "",
    vVendorName: "",
    vMobile: "",
    tNotes: "",
    eClienttype: "Company",
    vCity: "",
    vState: ""
  })
  const [compid, setcompid] = useState([]);
  const [conatctinputvalue, setConatctValue] = useState([]);
  const [selectedvalue, SetSelectedValue] = useState(null);
  const [Sourcelistdropdown, setSourcelistdropdown] = useState(sourcelist);
  const [technologydropdown] = useState(technologylist);
  const [contactperson, SetContactPerson] = useState({
    // iCompanyid: selectedvalue,
    // iAccountHolder: localStorage.getItem("Account Holder ID"),
    // vName: "",
    // vMobile: "",
    // vSkype: "",
    // vEmail: ""
  })
  const fetchData = () => {
    return axios.get(`${process.env.REACT_APP_APIURL}/vendor`, {}).then(result => {
      let res = result.data.vendorlist;
      setcompid(result.data.vendorlist)
      if (res === "No result found.") {
        // Empty result, return an empty array instead of a string
        // console.log("no result found")
        return [];
      }
      return res;
    })
  }

  const fetchConatctData = (vendorid) => {
    return axios({
      method: "get",
      url: `${process.env.REACT_APP_APIURL}/contactperson/${vendorid}`,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("Token")}`,
        "Content-Type": "application/json",
      },
    }).then(result => {

      let res = result.data.message;
      setConatctValue(res)
      // console.log("for the option from comnponay id", res)

      return res
    })
  }

  const handleChange = (e) => {
    const value = e.target.value
    const name = e.target.name
    // console.log(value,name)
    SetInquiryForm({ ...inquiryform, [name]: value })
    SetVendorForm({ ...vendorform, [name]: value })
    SetContactPerson({ ...contactperson, [name]: value })
    // console.log(name,value)
    // setVendorAdded(true)
    setPersonAdded(true)
    setPersonMessage(false)
    setVendorMessage(false)
  }

  const AddVendor = (e) => {
    alert("calling add compny")
    console.log("Company form ", e)
    // e.preventDefault()
    // setvError(isvalidCompany(vendorform))
  }
  const hereistoaddvendor = async () => {
    const data = {
      ...vendorform
    }
    const apidata = await axios({
      method: "post",
      url: `${process.env.REACT_APP_APIURL}/vendor/addvendor`,
      data: data,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("Token")}`,
        "Content-Type": "application/json",
      },
    })
    const respo = await apidata.data;
    const check = await respo?.Message;
    fetchData()

    if (apidata.status == 200) {
      // setVendorAdded(true)
      setVendorMessage("Added")
    }
  }
  useEffect(() => {
    fetchData()
  }, [])

  const AddContactPerson = (e) => {
    e.preventDefault()
  }
  const hereistoaddcontact = async () => {

    const data = {
      ...contactperson, iCompanyid: selectdcompany,
    }
    const apidata = await axios({
      method: "post",
      url: `${process.env.REACT_APP_APIURL}/contactperson`,
      data: data,
      headers: {
        Authorization: `Bearer ${localStorage.getItem("Token")}`,
        "Content-Type": "application/json",
      },
    })
    const respo = await apidata.data;
    const check = await respo?.Message;
    setPersonAdded(false)
    if (apidata.status == 200) {
      // setVendorAdded(true)
      setPersonMessage("Added")
    }


  }

  const handleimage = (e) => {
    SetMedia(e.target.files);
  }

  const inquiryData = (e) => {
    alert("inserting inquiry")
    console.log(e)
    debugger;
    // e.target.reset();
    // alert( JSON.stringify(inquiryform));
    const formData = new FormData();
    if (media != undefined) {
      for (let i = 0; i < media.length; i++) {
        formData.append("mediafiles", media[i]);
      }
    }

    formData.append("vTitle", inquiryform.vTitle);
    formData.append("vTechnology", inquiryform.vTechnology);
    formData.append("vSourceOfInquiry", inquiryform.vSourceOfInquiry);
    // formData.append("iAccountHolderId", inquiryform.iAccountHolderId);
    formData.append("eTypeofInquiry", inquiryform.eTypeofInquiry);
    formData.append("tDescription", inquiryform.tDescription);
    formData.append("vHireDuration", inquiryform.vHireDuration);
    formData.append("vHireMonthlyBudget", inquiryform.vHireMonthlyBudget);
    formData.append("vExperienceRequired", inquiryform.vExperienceRequired);
    formData.append("vendorid", inquiryform.vendorid);
    const apicall = async () => {

      const apidata = await axios({
        method: "post",
        url: `${process.env.REACT_APP_APIURL}/inquiry`,
        data: formData,
        headers: {
          Authorization: `Bearer ${localStorage.getItem("Token")}`,
          "Content-Type": "multipart/form-data",
        },
      })
      SetMedia(undefined);
      //  console.log(apidata)
      const respo = await apidata.data;
      const check = respo?.Message;
      if (apidata.status == 200) {
        setModal(true)
        setMessage("Inquiry Created")
      }
      else {
        setModal(true)
        setMessage("Something Went Wrong")
      }
    };
    try {
      apicall();

    } catch (e) {
      // alert(e)
    }
    SetMedia(null);
  }

  const handleClose = () => {
    setModal(false)
    refreshPage()

  }


  const handleVendorReset = () => {
    setError({})
    SetContactPerson({
      vName: "",
      vMobile: "",
      vSkype: "",
      vEmail: ""
    })
  }

  const handleCompanyReset = () => {
    setVendorMessage(false)
    SetVendorForm({
      vCompanyName: "",
      vAddress: "",
      vEmail: "",
      vVendorName: "",
      vMobile: "",
      tNotes: "",
      eClienttype: "Company",
      vCity: "",
      vState: ""
    })
  }
  console.log("array of string as sourcelist", sourcelist, technologydropdown)
  // const aquaticCreatures = ok?.map(e => ({ label: e.vCompanyName, value: e.id }));
  const {
    register,
    handleSubmit,
    reset,
    watch,
    formState: { errors: errors1 },
  } = useForm();
  const {
    register: register2,
    handleSubmit: handleSubmit2,
    reset: reset2,
    watch: watch2,
    // formState: {  errors: errors2 },
    formState: {
      errors: errors2
    },
  } = useForm();
  const selectdcompany=watch("vCompanyName")
  // eTypeofInquiry
  //   register: register2,
  // formState: { errors: errors2 },
  // handleSubmit: handleSubmit2,
  console.log("selectdcompany",selectdcompany)
  useEffect(()=>{

    if(selectdcompany)fetchConatctData(selectdcompany)
  },[selectdcompany])
  const companydetails = watch2("")
  console.log("companydetails", companydetails)
  // const companyerrors=formState2.errors
  // console.log("companyerrors",companyerrors)
  const selectedinquirytype = watch("eTypeofInquiry")
  const handleToDashboard = () => {
    if (localStorage.getItem("usertype") === "Sales") {
      navigate("/sales_dash")
    }
    else if (localStorage.getItem("usertype") === "Admin") {
      navigate("/admind")
    }
    else if (localStorage.getItem("usertype") === "Developer") {
      navigate("/devdash")
    }
  }
  console.log("errors", errors1, "errors2", errors2, "selectedinquirytype", selectedinquirytype)
  return (

    <>

      <div className='set'>
        <div className="container my-3 body p-5">
          <div className="row ">
            <div className="col-12 text-center mb-2 ">
              <h1 className="title">INQUIRY</h1>
            </div>
            <form onSubmit={handleSubmit(inquiryData)}>
              <div>
                <div className="row ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="" className="d-block">
                      Type of Inquire
                    </label>
                    <select
                      className="form-control rounded-pill mt-2"
                      {...register('eTypeofInquiry', { required: true })}
                    >
                      <option value="">Select</option>
                      <option value="Hire">Hire</option>
                      <option value="Project Basis">Project Basis</option>
                      {/* <option value="Fix Cost - Less than 2 Weeks">Fix Cost - Less than 2 Weeks</option> */}
                    </select>
                    {errors1.eTypeofInquiry && <p className="error">Field is required.</p>}
                  </div>
                  {selectedinquirytype==="Hire"&&
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="" className="d-block">
                      Type of Hire Inquire
                    </label>
                    <select
                      className="form-control rounded-pill mt-2"
                      {...register('eTypeofHireInquiry', { required: true })}
                    >
                      <option value="">Select</option>
                      <option value="Weekly">Weekly</option>
                      <option value="Monthly">Monthly</option>
                      <option value="Hourly">Hourly</option>
                    </select>
                    {errors1.eTypeofInquiry && <p className="error">Field is required.</p>}
                  </div>
}

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Title</label>
                    <input
                      {...register('vTitle', { required: true })}
                      type="text" className="form-control rounded-pill mt-2"
                    // name="vTitle" value={inquiryform.vTitle} onChange={handleChange} placeholder="Inquiry Title"
                    />
                    {errors1.vTitle && <p className="error">Field is required.</p>}
                  </div>
                {/* </div> */}
                {/* <div className="row "> */}
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="" className="d-block">
                      Technology
                    </label>

                    <select name="vTechnology" id=""
                      {...register('vTechnology', { required: true })}
                      className="form-control rounded-pill mt-2"

                    // value={inquiryform.vTechnology}
                    >
                      <option value="">select</option>
                      {technologydropdown.map((e) => {
                        return (
                          <option value={e} >{e}</option>
                        )
                      })}
                    </select>
                    {errors1.vTechnology && <p className="error">Field is required.</p>}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Source of Inquiry</label>
                    <select name="vSourceOfInquiry" id=""
                      {...register('vSourceOfInquiry', { required: true })}
                      className="form-control rounded-pill mt-2"

                    >
                      <option value="">select</option>
                      {Sourcelistdropdown.map((e) => {
                        console.log("here is source lisr", e)
                        return <option value={e}>{e}</option>
                      })
                      }
                    </select>
                    {errors1.vSourceOfInquiry && <p className="error">Field is required.</p>}

                  </div>
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Description</label>
                    <textarea
                      {...register('tDescription', { required: true })}
                      className="form-control rounded-pill mt-2"
                      placeholder="Description"></textarea>
                    {errors1.tDescription && <p className="error">Field is required.</p>}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor=""> Duration</label>

                    <input
                      {...register('vHireDuration', { required: true })}
                      type="number" className="form-control rounded-pill mt-2"
                      placeholder="Duration" />
                    {errors1.vHireDuration && <p className="error">Field is required.</p>}
                  </div>
                  <div className="col-6  my-2 px-3">
                    <label htmlFor=""> Monthly Budget</label>
                    <input
                      {...register('vHireMonthlyBudget', { required: true })}
                      type="number" className="form-control rounded-pill mt-2"
                      placeholder=" Monthly Budget" />
                    {inquiryform.vHireMonthlyBudget == null ? errors1.vHireMonthlyBudget && <p className="error">Field is required.</p> : ""}

                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Experience Required</label>
                    <input
                      {...register('vExperienceRequired', { required: true })}
                      type="text" className="form-control rounded-pill mt-2"
                      placeholder="Experience Required" />
                    {inquiryform.vExperienceRequired == null ? errors1.vExperienceRequired && <p className="error">Field is required.</p> : ""}

                  </div>
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Company Name</label>

                    <select

                      {...register('vCompanyName', { required: true })}
                      // onClickCapture={(e) => {
                      //   fetchConatctData(e.target.value)
                      //   SetSelectedValue(e.target.value)
                      //   setbtn(e.target.value)
                      // }}
                       className="form-control rounded-pill mt-2 my-2">
                      <option value="">Select company</option>
                      {compid &&
                        compid.map(e => {
                          return (
                            <option value={e.id}>{e.vCompanyName}</option>
                          )
                        })
                      }
                    </select>
                    {inquiryform.vCompanyName == null || undefined || null ? errors1.vCompanyName && <p className="error">Field is required.</p> : ""}
                    <button
                      type="button"
                      className="btn btn-outline-dark"
                      data-bs-toggle="modal"
                      data-bs-target="#exampleModal"
                      onClick={handleCompanyReset}
                    >
                      Add Company
                    </button>


                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Contact Person</label>
                    <select
                      {...register('vendorid', { required: true })}
                      className="form-control rounded-pill mt-2 my-2"
                    >
                      <option value="">Select...</option>
                      {
                        conatctinputvalue.map(e => {
                          return (
                            <option value={e.id}>{e.vName}</option>
                          )
                        })
                      }
                    </select>
                    {inquiryform.vendorid == null || undefined || null ? errors1.vendorid && <p className="error">Field is required.</p> : ""}

                    {selectdcompany ? <button
                      type="button"
                      className="btn btn-outline-dark"
                      data-bs-toggle="modal"
                      data-bs-target="#contactmodal"
                      onClick={handleVendorReset}

                    >
                      Add Contact Person
                    </button> :

                      <button
                        type="button"
                        className="btn btn-outline-dark"
                        data-bs-toggle="modal"
                        data-bs-target="#contactmodal"
                        onClick={handleVendorReset}
                        disabled

                      >
                        Add Contact Person
                      </button>
                    }


                  </div>
                  <div className="col-6  my-2  px-3">
                    <label htmlFor="">Documents</label>
                    {/* <input type="file" className=" col-12 mb-3" /> */}
                    <input

                      type="file" name="mediafiles" value={inquiryform.mediafiles} onChange={handleimage} className=" form-control rounded-pill mt-2" multiple />

                  </div>
                </div>
              </div>

              <button type="reset" onClick={handleToDashboard} className="mx-auto bg-dark text-white btn btn-outline-dark w-25">
                Back
              </button>
              <button type="submit" className="mx-auto btn btn-outline-dark w-25">
                Submit
              </button>
            </form>
          </div>
          <div
            className="modal fade"
            id="exampleModal"
            tabindex="-1"
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <form onSubmit={handleSubmit2(AddVendor)} >
                  <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">
                      Company Details
                    </h5>
                    <button
                      type="button"
                      className="btn-close"
                      data-bs-dismiss="modal"
                      aria-label="Close"
                    ></button>
                  </div>
                  <div className="modal-body">
                    <div>
                      <div className="form-check d-inline-flex mb-3">
                        <input
                          className=""
                          type="radio"
                          id="flexRadioDefault1"
                           value="Individual"
                          {...register2('eClienttype', { required: { value: true, message: "Please select Consultant or Company" } })}
                        />
                        <label
                          className="form-check-label"
                          for="flexRadioDefault1"
                        >
                          Consultant
                        </label>
                      </div>
                      <div className="form-check d-inline-flex mx-3">
                        <input
                          type="radio"
                          id="flexRadioDefault2"
                          // checked
                           value="Company" 
                          {...register2('eClienttype', { required: { value: true, message: "Please select Consultant or Company" } })}
                        />
                        <label
                          className="form-check-label"
                          for="flexRadioDefault2"
                        >
                          Company
                        </label>
                      </div>
                      {errors2.eClienttype ?
                        <p className='text-danger'>{errors2.eClienttype.message}</p> : ''}
                    </div>
                    <label htmlFor="">Company Name</label>
                    <input
                      {...register2('vCompanyName', { required: { value: true, message: "Please Enter Comany Name" } })}
                      type="text" className="form-control rounded-pill mt-2" />
                    {errors2.vCompanyName ?
                      <p className='text-danger'>{errors2.vCompanyName.message}</p> : ''}

                    <label htmlFor="">Address</label>
                    <input
                      {...register2('vAddress', { required: { value: true, message: "Please Enter Address" } })}
                      type="text" className="form-control rounded-pill mt-2" />
                    {errors2.vAddress ?
                      <p className='text-danger'>{errors2.vAddress.message}</p> : ''}
                    <label htmlFor="">Email</label>
                    <input
                      type="text" className="form-control rounded-pill mt-2"
                      {...register2('vEmail', {
                        required: { value: true, message: "Email is Required" }, pattern: {
                          value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                          message: "Please Enter Correct Email"
                        }
                      })}
                    />
                    {errors2.vEmail ?
                      <p className='text-danger'>{errors2.vEmail.message}</p> : ''}
                    <label htmlFor="">Vendor Name</label>
                    <input
                      type="text" className="form-control rounded-pill mt-2"
                      {...register2('vVendorName', { required: { value: true, message: "Please Enter Vendor Name" } })}
                    />
                    {errors2.vVendorName ?
                      <p className='text-danger'>{errors2.vVendorName.message}</p> : ''}
                    <label htmlFor="">Mobile</label>
                    <input
                      type="text" className="form-control rounded-pill mt-2"
                      {...register2('vMobile', {
                        required: { value: true, message: "Please Enter Contact Number" }, pattern: {
                          value: /^[0-9]{10}$/gm,
                          message: "Please Enter Contact Number Correctly"
                        }
                      })}
                    />
                    {errors2.vMobile ?
                      <p className='text-danger'>{errors2.vMobile.message}</p> : ''}
                    <label htmlFor="">Notes</label>
                    <input
                      type="text" className="form-control rounded-pill mt-2"
                      {...register2('tNotes')}
                    />
                    {errors2.tNotes ?
                      <p className='text-danger'>{errors2.tNotes.message}</p> : ''}
                    <label htmlFor="">City</label>
                    <input
                      type="text" className="form-control rounded-pill mt-2"
                      {...register2('vCity')}
                    />
                    {errors2.vCity ?
                      <p className='text-danger'>{errors2.vCity.message}</p> : ''}
                    <label htmlFor="">State</label>
                    <input
                      type="text" className="form-control rounded-pill mt-2"
                      {...register2('vState')}
                    />
                    {errors2.vState ?
                      <p className='text-danger'>{errors2.vState.message}</p> : ''}
                  </div>
                  <p style={{ color: "green", textAlign: "end", marginRight: "30px" }}>{vendorMessage}</p>
                  <div className="modal-footer">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      data-bs-dismiss="modal"
                      onClick={() => reset2()}
                    >
                      Close
                    </button>

                    <button type="submit" className="btn btn-outline-dark"
                    // data-bs-dismiss="modal"
                    // disabled={
                    //   errors2
                    // }
                    // onClick={AddVendor}
                    >
                      Save changes
                    </button>

                  </div>
                </form>
              </div>
            </div>
          </div>
          <div
            className="modal fade"
            id="contactmodal"
            tabindex="-1"
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">
                    Add Contact Person
                  </h5>
                  <button
                    type="button"
                    className="btn-close"
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  ></button>
                </div>
                <div className="modal-body">
                  {/* <select
                          name=""
                          id=""
                          className="formcontrol d-block dropdownWidth mb-3"
                        >
                          <option value="">Select...</option>
                        </select> */}
                  <select value={selectdcompany} name={contactperson.iCompanyid} onChange={(e) => fetchConatctData(e.target.value)}
                    disabled
                    className="form-control rounded-pill mt-2 my-2">
                    {compid &&
                      compid.map(e => {
                        return (
                          <option value={e.id}>{e.vCompanyName}</option>
                        )
                      })
                    }
                  </select>
                  <label htmlFor="">Name</label>
                  <input
                    type="text" className="form-control rounded-pill mt-2" name="vName" value={contactperson.vName} onChange={handleChange} />
                  {err.vName ? <p style={{ color: "red" }}>{err.vName}</p> : ""}
                  <label htmlFor="">Mobile</label>
                  <input
                    // {...register('vMobile', { required: true })}
                    type="text" className="form-control rounded-pill mt-2" name="vMobile" value={contactperson.vMobile} onChange={handleChange} />
                  {err.vMobile ? <p style={{ color: "red" }}>{err.vMobile}</p> : ""}
                  {/* {errors.vMobile && <p className="error">Field is required.</p>} */}
                  <label htmlFor="">Email</label>
                  <input
                    // {...register('vEmail', { required: true })}
                    type="text" className="form-control rounded-pill mt-2" name="vEmail" value={contactperson.vEmail} onChange={handleChange} />
                  {err.vEmail ? <p style={{ color: "red" }}>{err.vEmail}</p> : ""}
                  {/* {errors.vEmail && <p className="error">Field is required.</p>} */}
                  <label htmlFor="">Skype</label>
                  <input
                    // {...register('vSkype', { required: true })}
                    type="text" className="form-control rounded-pill mt-2" name="vSkype" value={contactperson.vSkype} onChange={handleChange} />
                  {err.vSkype ? <p style={{ color: "red" }}>{err.vSkype}</p> : ""}
                  {/* {errors.vSkype && <p className="error">Field is required.</p>} */}
                </div>
                <p style={{ color: "green", textAlign: "end", marginRight: "30px" }}>{personMessage}</p>

                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    data-bs-dismiss="modal"

                  >
                    Close
                  </button>
                  {personAdded ? <button type="button" onClick={AddContactPerson} className="btn btn-outline-dark"
                  // data-bs-dismiss="modal"
                  >
                    Save changes
                  </button> :

                    <button type="button" onClick={AddContactPerson} className="btn btn-outline-dark"
                      // data-bs-dismiss="modal"
                      disabled
                    >
                      Save changes
                    </button>
                  }
                </div>
              </div>
            </div>
          </div>
          {modal ?
            <>


              <div className='modal_'>
                <div className='modalBody'>

                  {message}

                  <div className="modal-button">
                    {/* <button className="modal-btn">Ok</button> */}
                    <button className="modal-btn" onClick={handleClose}>Ok</button>
                  </div>
                </div>

              </div>
            </>

            : ""}
        </div>
      </div>
    </>


  )
}
