import Sidebar from "../sidebar/sidebar";
import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useForm } from 'react-hook-form';
import { technologylist } from "../../constfiles";


const Project = () => {

  const navigate = useNavigate()
  const { inquiryid } = useParams();
  const [technologydropdown] = useState(technologylist);
  const [modal, setModal] = useState(false)
  const [message, setMessage] = useState(false)
  const [selectedFile, setSelectedFile] = useState();
  const [previoustinfo,setPreviousinfo]=useState({})

  const singleapi = async () => {
    try {
    // alert("fetcvhiong data")
    const result = await fetch(
      `${process.env.REACT_APP_APIURL}/inquiry/${inquiryid}`,
      {
        method: "get",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("Token")}`,
        },
      }
    );
    const apiresponse = await result.json();
    const check = apiresponse.inquirylist[0];
    console.log(check)
    setPreviousinfo({
      vTitleProject: check.vTitle,
      vTechnology: check.vTechnology,
      vHireDuration: check.vHireDuration,
      vHireMonthlyBudget: check.vHireMonthlyBudget,
      vExperienceRequired: check.vExperienceRequired,
      eStatus: check.eStatus,
      eTypeOfProject: check.eTypeofInquiry
    })
      setValue({
        vTitleProject: check.vTitle,
        vTechnology: check.vTechnology,
        vHireDuration: check.vHireDuration,
        vHireMonthlyBudget: check.vHireMonthlyBudget,
        vExperienceRequired: check.vExperienceRequired,
        eStatus: check.eStatus,
        eTypeOfProject: check.eTypeofInquiry
      })
    }
    catch (e) {
      console.log("fetching error",e)
    }
  };
  useEffect(() => {
    singleapi(inquiryid);
  }, []);
  const createproject = (e) => {
    const formData = new FormData();
    if (selectedFile !== undefined) {

      for (let i = 0; i < selectedFile.length; i++) {
        formData.append("requiredprojectfiles", selectedFile[i]);
      }
    }
    formData.append("iiInquiryId", inquiryid);
    formData.append("eTypeOfProject", e.eTypeOfProject);
    formData.append("vTitleProject", e.vTitleProject);
    formData.append("fCostOfProject", e.fCostOfProject);
    formData.append("vTechnology", e.vTechnology);
    formData.append("vHireResource", e.vHireResource);
    // formData.append("iVendorId", e.iVendorId);
    formData.append("dBillingCycleDate", e.dBillingCycleDate);
    formData.append("fHireBillingAmount", e.fHireBillingAmount);
    formData.append("dconvertedDate", e.dconvertedDate);
    formData.append("fProjectCost", e.fProjectCost);
    formData.append("iAccountHolderId", localStorage.getItem("userid"));
    formData.append("tNotes", e.tNotes);
    formData.append("vHireDuration", e.vHireDuration);
    formData.append("vHireMonthlyBudget", e.vHireMonthlyBudget);
    formData.append("vExperienceRequired", e.vExperienceRequired);
    formData.append("fAtCostResourceToVendor", e.fAtCostResourceToVendor);
    formData.append("eProjectStatus", e.eProjectStatus);
    formData.append("eStatus", e.eStatus);
    formData.append("dstartdate", e.dstartdate);
    formData.append("denddate", e.denddate);
    // const imageupdateapicall = async () => {
    //   let callingurl = `${process.env.REACT_APP_APIURL}/projects`;
    //   const returneddetail = await axios({
    //     method: "post",
    //     url: callingurl,
    //     data: formData,
    //     headers: {
    //       Authorization: `Bearer ${localStorage.getItem("Token")}`,
    //       "Content-Type": "multipart/form-data",
    //     },
    //   });
    //   setSelectedFile(undefined);
    //   const respo = returneddetail.data;
    //   const check = respo?.Message;
    //   // alert("check", JSON.stringify(check));

    //   if (returneddetail.status == 200) {
    //     setModal(true)
    //     setMessage("Project Created")
    //   }
    //   else {
    //     setModal(true)
    //     setMessage("Something Went Wrong")
    //   }
    // };
    try {

    } catch (e) {
      // alert(e)
    }
    setSelectedFile(null);

  };
  const uploadimage = (e) => {
    setSelectedFile(e.target.files);
  };
  const handleBack = () => {
    navigate("/i_edit/" + `${inquiryid}`)
  }
  const {
    register,
    handleSubmit,
    setValue,
    formState: { errors },
  } = useForm({
    defaultValues:previoustinfo
  });




  return (
    <>
      <Sidebar IsSales={true} />
      <div className='set'>
        <div className="container my-3 body p-5">
          <button type="submit" onClick={handleBack} className="mx-auto bg-dark text-white btn btn-outline-dark w-25">
            Back to Inquiry
          </button>
          <div className="row ">
            <form onSubmit={handleSubmit(createproject)}>
              <div className="col-12 text-center mb-2 ">
                <h1 className="title">PROJECT</h1>
              </div>
              <div>
                <div className="col-xxl-12 col-xl-12 col-lg-6 col-md-12 col-sm-12 my-2 d-inline-flex ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="" className="d-block">
                      Type of Project
                    </label>
                    <select
                      {...register('eTypeOfProject', { required: {
                        value:true,
                        message:"Type of Project is required"
                      } })}
                      className="form-control rounded-pill mt-2"
                      type="text"
                      placeholder="Type of Project"
                    >
                      <option value="">Select</option>
                      <option value="Hire">Hire</option>
                      <option value="Project Basis">Project Basis</option>
                    </select>
                    {errors.eTypeOfProject ? <p style={{ color: "red" }}>{errors.eTypeOfProject}</p> : ""}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Title</label>
                    <input
                       {...register('vTitleProject', { required: {
                        value:true,
                        message:"Title is Required"
                       } })}
                      className="form-control rounded-pill mt-2"
                      type="text"
                      placeholder="Project Title"
                    />
                    {errors.vTitleProject ? <p style={{ color: "red" }}>{errors.vTitleProject}</p> : ""}
                  </div>
                </div>
                <div className="col-12 my-2 d-inline-flex ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="" className="d-block">
                      Technology
                    </label>
                    <select 
                      {...register('vTechnology', { required: {
                        value:true,
                        message:"Technology is Required"
                      } })}
                      className="form-control rounded-pill mt-2"
                    >
                      <option value="">select</option>
                      {technologydropdown.map((e) => {
                        return (
                          <option value={e}>{e}</option>
                        )
                      })}
                    </select>
                    {errors.vTechnology ? <p style={{ color: "red" }}>{errors.vTechnology}</p> : ""}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Hire Resource</label>
                    <input
                      {...register('vHireResource', { required: {
                        value:true,
                        message:"Hire Resource is Required"
                      } })}
                      type="text"
                      className="form-control rounded-pill mt-2"
                      placeholder="Hire Resource"
                    />
                    {errors.vHireResource ? <p style={{ color: "red" }}>{errors.vHireResource}</p> : ""}
                  </div>
                </div>
                <div className="col-xxl-12 col-xl-12 col-lg-12 col-md-6 col-sm-6 my-2 d-inline-flex  ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Billing Date</label>
                    {/* <textarea className="inputField col-12 mb-3" name="tDescription" value={inquiryform.tDescription} onChange={handleChange} placeholder="Description"></textarea> */}
                    <input
                      {...register('dBillingCycleDate', { required: {
                        value:true,
                        message:"Billing Date is Required"
                      }  })}
                      type="date"
                      className="form-control rounded-pill mt-2"
                      placeholder="Billing Date"
                    />
                    {errors.dBillingCycleDate ? <p style={{ color: "red" }}>{errors.dBillingCycleDate}</p> : ""}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Billing Amount</label>
                    <input
                       {...register('fHireBillingAmount', { required: {
                        value:true,
                        message:"Billing Amount is Required"
                      }  })}
                      type="number"
                      className="form-control rounded-pill mt-2"
                      placeholder="Billing Amount"
                    />
                    {errors.fHireBillingAmount ? <p style={{ color: "red" }}>{errors.fHireBillingAmount}</p> : ""}
                  </div>
                </div>
                <div className="col-xxl-12 col-xl-12 col-lg-12 col-md-6 col-sm-6 my-2 d-inline-flex  ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Converted Date</label>
                    <input
                      {...register('dconvertedDate', { required: {
                        value:true,
                        message:"Converted Date is Required"
                      }  })}
                      type="date"
                      className="form-control rounded-pill mt-2"
                      name="dconvertedDate"
                      placeholder="Converted Date"
                    />
                    {errors.dconvertedDate ? <p style={{ color: "red" }}>{errors.dconvertedDate}</p> : ""}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Project Cost</label>
                    <input
                      {...register('fProjectCost', { required: {
                        value:true,
                        message:"Project Cost is Required"
                      }  })}
                      type="number"
                      className="form-control rounded-pill mt-2"
                      name="fProjectCost"
                      placeholder="Project Cost"
                    />
                    {errors.fProjectCost ? <p style={{ color: "red" }}>{errors.fProjectCost}</p> : ""}
                  </div>
                </div>


                <div className="col-xxl-12 col-xl-12 col-lg-12 col-md-6 col-sm-6 my-2 d-inline-flex  ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Start Date</label>
                    <input
                      {...register('dstartdate', { required: {
                        value:true,
                        message:"Start Date is Required"
                      }  })}
                      type="date"
                      className="form-control rounded-pill mt-2"
                      placeholder="Converted Date"
                    />
                    {errors.dstartdate ? <p style={{ color: "red" }}>{errors.dstartdate}</p> : ""}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">End Date</label>
                    <input
                      {...register('denddate', { required: {
                        value:true,
                        message:"End Date is Required"
                      }  })}
                      type="date"
                      className="form-control rounded-pill mt-2"
                      placeholder="End Date"
                    />
                    {errors.denddate ? <p style={{ color: "red" }}>{errors.denddate}</p> : ""}
                  </div>
                </div>



                <div className="col-xxl-12 col-xl-12 col-lg-12 col-md-6 col-sm-6 my-2 d-inline-flex  ">
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Cost of Project</label>

                    <input
                      {...register('fCostOfProject', { required: {
                        value:true,
                        message:"Cost Of Project is Required"
                      } })}
                      type="number"
                      className="form-control rounded-pill mt-2"
                      placeholder="Cost of Project"
                    />
                    {errors.fCostOfProject ? <p style={{ color: "red" }}>{errors.fCostOfProject}</p> : ""}
                  </div>

                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Notes</label>
                    <input
                       {...register('tNotes', { required: {
                        value:true,
                        message:"End Date is Required"
                      } })}
                      type="text"
                      className="form-control rounded-pill mt-2"
                      placeholder="Notes"
                    />
                    {errors.tNotes ? <p style={{ color: "red" }}>{errors.tNotes}</p> : ""}
                  </div>
                </div>



                <div className="col-xxl-12 col-xl-12 col-lg-12 col-md-6 col-sm-6 my-2 d-inline-flex  ">


                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Hire Duration</label>
                    <input
                      {...register('vHireDuration', { required: {
                        value:true,
                        message:"Hire Duration is Required"
                      } })}
                      type="text"
                      className="form-control rounded-pill mt-2"
                      placeholder="Hire Duration"
                    />
                    {errors.vHireDuration ? <p style={{ color: "red" }}>{errors.vHireDuration}</p> : ""}
                  </div>
                  <div className="col-6  my-2 px-3">
                    <label htmlFor="">Monthly Budget</label>
                    <input
                      {...register('vHireMonthlyBudget', { required: {
                        value:true,
                        message:"End Date is Required"
                      } })}
                      type="number"
                      className="form-control rounded-pill mt-2"
                      name="vHireMonthlyBudget"
